mapper.select:

where asset_row_id = #{rowId}
order by asset_row_id asc
limit 1 offset 0

mapper.update:
update assets
asset_name = #{name}, asset_type_id = #{typeId}, asset_owner_id = #{ownerId}, asset_location = ST_Multi(ST_GeomFromGeoJSON(#{locationAsJson})), asset_capacity_litres = #{capacityLitres}, asset_material = #{material}, asset_status = #{status}, asset_installed_at = #{installedAt}, asset_last_inspection = #{lastInspection}, asset_last_modified = #{lastModified},
where asset_id = #{id};

update asset_types
asset_type_name = #{name}, asset_type_description = #{description},
where asset_type_type_id = #{id};
