mapper.select:
select ST_Intersects(asset_location, warning_area)
from assets
         inner join weather_warnings on ST_Intersects(asset_location, warning_area)
where asset_id = #{assetId}
  and warning_id = #{warningId}

mapper.update:
update weather_warnings
warning_weather_type = #{weatherType}, warning_level = #{warningLevel}, warning_head_line = #{warningHeadLine}, warning_valid_from = #{validFrom}, warning_valid_to = #{validTo}, warning_impact = #{warningImpact}, warning_likelihood = #{warningLikelihood}, warning_affected_areas = #{affectedAreas}, warning_what_to_expect = #{whatToExpect}, warning_further_details = #{warningFurtherDetails}, warning_update_description = #{warningUpdateDescription}, warning_area = ST_Multi(ST_GeomFromGeoJSON(#{areaAsJson})),
where warning_id = #{id};

update templates
set templates_message = #{message}
where templates_id = #{id};

mapper.select:
select templates_message
from templates
where templates_asset_type = #{assetType} and templates_weather_warning_type = #{weatherType} and templates_severity = #{severity}

mapper.update:
update templates
set templates_message = #{message}
where templates_asset_type = #{assetType} and templates_weather_warning_type = #{weatherType} and templates_severity = #{severity}
